<template>  <section class="page">    <v-header></v-header>    <main class="main">      <section class="left-content">        <v-nav></v-nav>      </section>      <section :class="['right-content', { hasPreview: !$route.meta.noPreview }]">        <div class="markdown-body">          <router-view></router-view>        </div>      </section>    </main>    <preview v-if="$route.meta.preview" :width="preview_size[0]" :height="preview_size[1]">      <iframe class="preview" :src="$route.meta.preview" frameborder="0"></iframe>    </preview>  </section></template><style lang="stylus" scoped>  @import '../../style/index.styl';  _left_width = 240px;  .page {    display: flex;    flex-direction: column;    min-height: 100vh;  }  .main {    display: flex;    flex: 1;  }  .left-content {    display: flex;    width: _left_width;    flex-shrink: 0;    border-right: 1px solid #ccc;  }  .right-content {    flex: 1;    padding: 10px 30px;    &.hasPreview {      margin-right: 470px;    }    .markdown-body table code {      color: _blue_deep;    }  }  .preview {    width: 100%;    height: 100%;  }</style><script>  export default {    name: 'home',    components: {      vHeader: require('../components/Header.vue'),      vNav: require('../components/Nav.vue'),      preview: require('../components/preview.vue')    },    data () {      return {        phone5_size: [320, 568],        phone6_size: [375, 667]      }    },    computed: {      preview_size () {        return document.documentElement.clientHeight < (this.phone6_size[1] + 120) ? this.phone5_size : this.phone6_size      }    }  }</script>